
public class SwapNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int x,y;
    x=10;
    y=20;
    System.out.println("Before swapping values are - x:"+x+" "+"Y:"+y);
    x=x+y;
    y=x-y;
    x=x-y;
    System.out.println("after swapping values are - x:"+x+" "+"Y:"+y);
	}

}
